import React, { useCallback, useState } from "react";
import axios from "axios";


   
   function updateInfo(data) {
     document.getElementById("randomCharName").innerText = data.name;
     document.getElementById("height").innerText = `Height: ${data.height}`;
     document.getElementById("mass").innerText= `Mass: ${data.mass}`;
     document.getElementById("gender").innerText = `Gender: ${data.gender}`;
   }
 
   function updateInfoWithError(data) {
     document.getElementById("randomCharName").innerText = 'Ups, this Character is on a vacation now.'
     document.getElementById("height").innerText = ''
     document.getElementById("mass").innerText= ''
     document.getElementById("gender").innerText = ''
   }
   function loading(data) {
     document.getElementById("randomCharName").innerHTML = '<i class="fas fa-circle-notch fa-spin"></i>'
     document.getElementById("height").innerText = ''
     document.getElementById("mass").innerText= ''
     document.getElementById("gender").innerText = ''
   }

const initialState = {
    name: '',
    height: '',
    mass: '',
    gender: ''
}
function Random() {

    const [character, setCharacter] = useState(initialState);
    const getInfo = useCallback(() => {
        loading()
        var randNum = Math.floor(Math.random()*82 + 1);
        var url = 'https://swapi.dev/api/people/' + randNum;
         axios.get(url).then(function(response) {
            setCharacter(response.data)
         }).catch(e => {
            setCharacter(initialState);
         })
       },[]);

    return(
        <div className="random-div">
        <h1>Random Character</h1>
        <div className="info-container">
         <h2 id="randomCharName" className="mt-4">Name: {character.name}</h2>
          <p id="height">Height: {character.height}</p>
          <p id="mass">Mass: {character.mass}</p>
          <p id="gender">Gender: {character.gender}</p>
        </div>
        <button id="randomCharButton" onClick={getInfo} >Get a random Character</button>
      </div>
    )
} 
export default Random;
