import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Card,
  Grid,
  GridColumn,
  CardContent,
  CardHeader,
  CardDescription,
} from "semantic-ui-react";

function Films() {
  const [films, setFilms] = useState([]);
  useEffect(() => {
    var url = "https://swapi.dev/api/films";
    async function fetchFilms() {
      let res = await fetch(url);
      let data = await res.json();
      setFilms(data.results);
    }
    fetchFilms();
  }, []);

  var allPeople;

  function getAllStarwarsPeople() {
    let people = [];
    return axios("https://swapi.dev/api/people/")
      .then((response) => {
        people = response.data.results;
        return response.data.count;
      })
      .then((count) => {
        const numberOfPagesLeft = Math.ceil((count - 1) / 10);
        let promises = [];
        for (let i = 2; i <= numberOfPagesLeft; i++) {
          promises.push(axios(`https://swapi.dev/api/people?page=${i}`));
        }
        return Promise.all(promises);
      })
      .then((response) => {
        people = response.reduce(
          (acc, data) => [...acc, ...data.data.results],
          people
        );
        return people;
      })
      .catch((error) => console.log("Error catching people"));
  }

  (async () => {
    const starwarsPeople = await getAllStarwarsPeople();
    for (let i = 0; i < starwarsPeople.length; i++) {
      //console.log(starwarsPeople[i].name);
    }
    allPeople = starwarsPeople;
  })();

  let match;
  let matchResult;

  function handleChange(event) {
    match = event.target.value;
  }

  function find() {
    const result = allPeople.filter(e => e.name === match);
    matchResult = result[0];
    console.log(result[0]);
  }

  return (
    <>
      <h1>Films</h1>
      {
        <Grid columns={3}>
          {films.map((films, i) => {
            return (
              <GridColumn key={i}>
                <Card>
                  <CardContent>
                    <CardHeader>Episode: {films.episode_id}</CardHeader>
                    <CardDescription>
                      <strong>Title</strong>
                      <p>{films.title}</p>
                    </CardDescription>
                  </CardContent>
                </Card>
              </GridColumn>
            );
          })}
        </Grid>
      }
      <div className="search-div">
      <h1>Searhing</h1>
        <input className="search-input" onChange={handleChange} type="text" />
        <button id="findButton" onClick={find}>
          Find
        </button>
      </div>
      <div>
        <h1>{result[0].name}</h1>
        <p>{result[0].height}</p>
      </div>
    </>
  );
}
export default Films;
