import React, { useState } from "react";
import axios from "axios";
import debounce from "lodash/debounce";


/* const debounce = (fn, mili) => {
  let id;
  return (...args) => {
    clearInterval(id);
    id = setInterval(() => fn(...args), mili);
  };
}; */

const find = debounce(
  (setSearchResult, input) =>
    axios("https://swapi.dev/api/people/?search=" + input).then((res) =>
      setSearchResult(res.data.results)
    ),
  300
);

let visible = true;
function clear() {
    visible=false;
    document.getElementsByClassName('search-input')[0].value = '';
   //
   document.getElementsByTagName("h1").style.display = "none";
}

function Search() {
  const [searchResult, setSearchResult] = useState([]);

  function handleChange(event) {
    visible=true;
    find(setSearchResult, event.target.value);
  }

  return (
    <>
      <div className="search-div">
        <h1>Search People</h1>
        <input className="search-input" onChange={handleChange} onfocus="this.value='' " type="text" />
        <button className="clearButton" onClick={clear}>
          Clear
        </button>
      </div>
      { visible &&
        searchResult.map((matchResult) => (
        <div id="sr-div" className="search-result-div">
          <h1>{matchResult.name}</h1>
        </div>
      ))}
    </>
  );
}
export default Search;
