import React, { useState, useEffect } from "react";
import {
  Card,
  Grid,
  GridColumn,
  CardContent,
  CardHeader,
  CardDescription,
} from "semantic-ui-react";
import { Container, Dimmer, Loader } from "semantic-ui-react";

function People() {
  var [pageNum, setpageNum] = useState(1);
  var url = "https://swapi.dev/api/people/?page=" + pageNum;
  const [loading, setLoading] = useState(true);

  const [people, setPeople] = useState([]);

  set
  useEffect(() => {
    async function fetchPeople() {
      let res = await fetch(url);
      let data = await res.json();
      setPeople(data.results);
      setLoading(false);
    }
    fetchPeople();
  });

  function Add() {
    if (pageNum < 9) {
      pageNum++;
      setpageNum(pageNum);
      url = "https://swapi.dev/api/people/?page=" + pageNum;
      console.log(url);
    }
  }
  function Prev() {
    if (pageNum > 1) {
      pageNum--;
      setpageNum(pageNum);
      url = "https://swapi.dev/api/people/?page=" + pageNum;
      console.log(url);
    }
  }

  return (
    <>
      <h1>People</h1>
      <Grid columns={3}>
        {people.map((people, i) => {
          return (
            <GridColumn key={i}>
              <Card>
                <CardContent>
                  <CardHeader>{people.name}</CardHeader>
                  <CardDescription>
                    <strong>Height</strong>
                    <p>{people.height}</p>
                    <strong>Mass</strong>
                    <p>{people.mass}</p>
                    <strong>Gender</strong>
                    <p>{people.gender}</p>
                  </CardDescription>
                </CardContent>
              </Card>
            </GridColumn>
          );
        })}
      </Grid>

      <div id="prev-next-div">
        <a id="prevButton" onClick={Prev} className="next round">
          &#8249;
        </a>
        <a id="nextButton" onClick={Add} className="next round">
          &#8250;
        </a>
      </div>
    </>
  );
}
export default People;
