import React, { useState, useEffect, useCallback } from "react";
import {
  Card,
  Grid,
  GridColumn,
  CardContent,
  CardDescription,
} from "semantic-ui-react";
import {Menu, Container} from "semantic-ui-react";
import {Link} from "react-router-dom";
import debounce from "lodash/debounce";

function People({character}) {
  var [pageNum, setpageNum] = useState(1);
  const [people, setPeople] = useState([]);
  useEffect(() => {
    var url = "https://swapi.dev/api/people/?page=" + pageNum;
    async function fetchPeople() {
      let res = await fetch(url);
      let data = await res.json();
      setPeople(data.results);
    }
    fetchPeople();
  }, [pageNum]);

  console.log(data);

  const Add = useCallback(
    debounce(() => {
      if (pageNum < 9) {
        pageNum++;
        setpageNum(pageNum);
      }
    }, 300),
    []
  );
  const Prev = useCallback(
    debounce(() => {
      if (pageNum > 1) {
        pageNum--;
        setpageNum(pageNum);
      }
    }, 300),
    []
  );

  return (
    <>
      <h1>People</h1>
      <Grid columns={3}>
        {people.map((people, i) => {
          return (
            <GridColumn key={i}>
              <Card>
                <CardContent>
                  <CardDescription>
                    <Link to="/character">
                      <Menu.Item className="cardHeader" name={people.name} />
                    </Link>
                    <strong className="cardContent">Height</strong>
                    <p className="cardContent">{people.height}</p>
                    <strong className="cardContent">Mass</strong>
                    <p className="cardContent">{people.mass}</p>
                    <strong className="cardContent">Gender</strong>
                    <p className="cardContent">{people.gender}</p>
                  </CardDescription>
                </CardContent>
              </Card>
            </GridColumn>
          );
        })}
      </Grid>
      <div id="prev-next-div">
        <a id="prevButton" onClick={Prev} className="next round">
          &#8249;
        </a>
        <a id="nextButton" onClick={Add} className="next round">
          &#8250;
        </a>
      </div>
    </>
  );
}
export default People;
