import React, { useState, useEffect } from "react";
import {Card, Grid, GridColumn, CardContent, CardHeader, CardDescription} from "semantic-ui-react";

function Starships() {

    var [pageNum, setpageNum] = useState(1);
    var url = "https://swapi.dev/api/starships/?page=" + pageNum;
   
    const [starships, setStarships] = useState([]);
    useEffect(() => {
        async function fetchStarships() {
            let res = await fetch(url);
            let data = await res.json();
            setStarships(data.results);
          }
          fetchStarships();
    })

    function Add() {
        if(pageNum < 4) {
         pageNum++;
         setpageNum(pageNum)
         url = "https://swapi.dev/api/starships/?page=" + pageNum;
         console.log(url);
        }
    }
    function Prev() {
        if(pageNum > 1) {
         pageNum--;
         setpageNum(pageNum)
         url = "https://swapi.dev/api/starships/?page=" + pageNum;
         console.log(url);
        }
    }

    return (
        <>
            <h1>Starships</h1>
            <Grid columns={3}>
                {starships.map((starships,i) => {
                    return(
                        <GridColumn key={i}>
                            <Card>
                                <CardContent>
                                    <CardDescription>
                                        h1
                                        <strong>Name</strong>
                                        <p>{starships.name}</p>
                                        <strong>Model</strong>
                                        <p>{starships.model}</p>
                                        <strong>Lenght</strong>
                                        <p>{starships.length}</p>
                                    </CardDescription>
                                </CardContent>
                            </Card>
                        </GridColumn>
                    )
                })}
            </Grid>
            <div id="prev-next-div">
                 <a id="prevButton"  onClick={Prev} className="next round">&#8249;</a>
                 <a id="nextButton"  onClick={Add} className="next round">&#8250;</a>
            </div>
        </>
    )
}
export default Starships;
