                    return(
                        <GridColumn key={i}>
                            <Card>
                                <CardContent>
                                    <CardHeader>{people.name}</CardHeader>
                                    <CardDescription>
                                        <strong>Height</strong>
                                        <p>{people.height}</p>
                                        <strong>Mass</strong>
                                        <p>{people.mass}</p>
                                        <strong>Gender</strong>
                                        <p>{people.gender}</p>
                                    </CardDescription>
                                </CardContent>
                            </Card>
                        </GridColumn>
                    )
                })}
            </Grid>
            <div id="prev-next-div">
                 <a id="prevButton" href="http://localhost:3000/people/?page=2" onClick={asd} className="next round">&#8249;</a>
                 <a id="nextButton" href="http://swapi.dev/api/people/?page=3" onClick={asd} className="next round">&#8250;</a>
            </div>
            
        </>
    )
}
export default People;
