import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Card,
  Grid,
  GridColumn,
  CardContent,
  CardHeader,
  CardDescription,
} from "semantic-ui-react";

function Films() {
  const [films, setFilms] = useState([]);
  useEffect(() => {
    var url = "https://swapi.dev/api/films";
    async function fetchFilms() {
      let res = await fetch(url);
      let data = await res.json();
      setFilms(data.results);
    }
    fetchFilms();
  }, []);



  const [searchResult, setSearchResult] = useState([]);
    useEffect(() => {
    
      var url = "https://swapi.dev/api/people/" + number;
      async function fetchAll() {
        let res = await fetch(url);
        let allData = await res.json();
        setSearchResult(allData.results);
        tomb.push(allData.name);

        console.log(allData);
      }
      fetchAll();
    }, []);
  function find() {
    alert("helo");
  }

  function getDetail() {
    var apiURL = "https://swapi.co/api/people";
    axios.get(apiURL).then(function(response) {
      showDetail(response.data);
    });
  }
  
  function showDetail(data) {
    for (let i = 0; i < data.results.length; i++) {
      console.log(data.results[i].name);
    }
  }
  getDetail();

  return (
    <>
      <h1>Films</h1>
      {
        <Grid columns={3}>
          {films.map((films, i) => {
            return (
              <GridColumn key={i}>
                <Card>
                  <CardContent>
                    <CardHeader>Episode: {films.episode_id}</CardHeader>
                    <CardDescription>
                      <strong>Title</strong>
                      <p>{films.title}</p>
                    </CardDescription>
                  </CardContent>
                </Card>
              </GridColumn>
            );
          })}
        </Grid>
      }
      <div className="search-div">
        <h1>Searhing</h1>
        <input className="search-input" type="text" />
        <button id="findButton" onClick={find}>
          Find
        </button>
      </div>
    </>
  );
}
export default Films;
