import React, { useState, useEffect } from "react";
import {Card, Grid, GridColumn, CardContent, CardHeader, CardDescription} from "semantic-ui-react";
 
function Planets() {

    var [pageNum, setpageNum] = useState(1);
    var url = "https://swapi.dev/api/planets/?page=" + pageNum;
   
    const [planets, setPlanets] = useState([]);
    useEffect(() => {
        async function fetchPlanets() {
            let res = await fetch(url);
            let data = await res.json();
            setPlanets(data.results);
          }
          fetchPlanets();
    })

    function Add() {
        if(pageNum < 6) {
         pageNum++;
         setpageNum(pageNum)
         url = "https://swapi.dev/api/planets/?page=" + pageNum;
         console.log(url);
        }
    }
    function Prev() {
        if(pageNum > 1) {
         pageNum--;
         setpageNum(pageNum)
         url = "https://swapi.dev/api/planets/?page=" + pageNum;
         console.log(url);
        }
    }

    return (
        <>
            <h1>Planets</h1>
           { <Grid columns={3}>
                {planets.map((planets,i) => {
                    return(
                        <GridColumn key={i}>
                            <Card>
                                <CardContent>
                                    <CardDescription>
                    <h1  className="cardHeader">{}</h1>
                                        <strong>Climate</strong>
                                        <p>{planets.climate}</p>
                                        <strong>Gravity</strong>
                                        <p>{planets.gravity}</p>
                                        <strong>Terrain</strong>
                                        <p>{planets.terrain}</p>
                                        <strong>Population</strong>
                                        <p>{planets.population}</p>
                                    </CardDescription>
                                </CardContent>
                            </Card>
                        </GridColumn>
                    )
                })}
            </Grid>}
             <div id="prev-next-div">
                 <a id="prevButton"  onClick={Prev} className="next round">&#8249;</a>
                 <a id="nextButton"  onClick={Add} className="next round">&#8250;</a>
            </div>
            
        </>
    )
}
export default Planets;
