import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Card,
  Grid,
  GridColumn,
  CardContent,
  CardHeader,
  CardDescription,
} from "semantic-ui-react";

function Films() {
  const [films, setFilms] = useState([]);
  useEffect(() => {
    var url = "https://swapi.dev/api/films";
    async function fetchFilms() {
      let res = await fetch(url);
      let data = await res.json();
      setFilms(data.results);
    }
    fetchFilms();
  }, []);



  const [searchResult, setSearchResult] = useState([]);
    useEffect(() => {
    
      var url = "https://swapi.dev/api/people/";
      async function fetchAll() {
        let res = await fetch(url);
        let allData = await res.json();
        setSearchResult(allData.results);

        for(let i=0 ;i<allData.results.length;i++) {
            console.log(allData.results[i].name);
        }
      }
      fetchAll();
    }, []);

    function getAllStarwarsPeople() {
        let people = [];
        // first page
        return axios("https://swapi.co/api/people/")
            .then(response => {
                // collect people from first page
                people = response.data.results;
                return response.data.count;
            })
            .then(count => {
                // exclude the first request
                const numberOfPagesLeft = Math.ceil((count - 1) / 10);
                let promises = [];
                // start at 2 as you already queried the first page
                for (let i = 2; i <= numberOfPagesLeft; i++) {
                    promises.push(axios(`https://swapi.co/api/people?page=${i}`));
                }
                return Promise.all(promises);
            })
            .then(response => {
                //get the rest records - pages 2 through n.
                people = response.reduce((acc, data) => [...acc, ...data.data.results], people);
                return people;
            })
            .catch(error => console.log("Properly handle your exception here"));
    }
    
    (async () => {
        const starwarsPeople = await getAllStarwarsPeople();
        console.log("starwarsPeople", starwarsPeople.length);
    })();

    

  function find() {
    alert("helo");
  }


  return (
    <>
      <h1>Films</h1>
      {
        <Grid columns={3}>
          {films.map((films, i) => {
            return (
              <GridColumn key={i}>
                <Card>
                  <CardContent>
                    <CardHeader>Episode: {films.episode_id}</CardHeader>
                    <CardDescription>
                      <strong>Title</strong>
                      <p>{films.title}</p>
                    </CardDescription>
                  </CardContent>
                </Card>
              </GridColumn>
            );
          })}
        </Grid>
      }
      <div className="search-div">
        <h1>Searhing</h1>
        <input className="search-input" type="text" />
        <button id="findButton" onClick={find}>
          Find
        </button>
      </div>
    </>
  );
}
export default Films;
