import React, { useState, useEffect } from "react";
import axios from "axios";
import { debounce } from "lodash";


const find = debounce((setSearchResult, input) => {
    axios("https://swapi.dev/api/people/?search=" + input).then((res) =>
      setSearchResult(res.data.results)
    );
},300)

function Search() {
 
  const [input, setInput] = useState("");
  const [searchResult, setSearchResult] = useState([]);

  function handleChange(event) {
    find(setSearchResult, event.target.value);
  }


  return (
    <>
      <div className="search-div">
        <h1>Search People</h1>
        <input className="search-input" onChange={handleChange} type="text" />
        <button id="findButton" onClick={find}>
          Find
        </button>
      </div>
      {searchResult.map((matchResult) => (
        <div className="search-result-div">
          <h1>Name: {matchResult.name}</h1>
          <p>Gender: {matchResult.gender}</p>
          <p>Height: {matchResult.height}</p>
        </div>
      ))}
    </>
  );
}
export default Search;
