import React, { useState } from "react";
import axios from "axios";

const debounce = (fn, mili) => {
  let id;
  return (...args) => {
    clearInterval(id);
    id = setInterval(() => fn(...args), mili);
  };
};

const find = debounce(
  (setSearchResult, input) =>
    axios("https://swapi.dev/api/people/?search=" + input).then((res) =>
      setSearchResult(res.data.results)
    ),
  300
);

function Search() {
  const [searchResult, setSearchResult] = useState([]);

  function handleChange(event) {
    visible = true;
    find(setSearchResult, event.target.value);
  }

  let visible = true;
  function clear() {
    visible = false;
  }

  return (
    <>
      <div className="search-div">
        <h1>Search People</h1>
        <input className="search-input" onChange={handleChange} type="text" />
        <button id="findButton" onClick={clear}>
          Find
        </button>
      </div>
      {visible &&
        searchResult.map((matchResult) => (
          <div id="sr-div" className="search-result-div">
            <h1>{matchResult.name}</h1>
            <p>Gender: {matchResult.gender}</p>
            <p>Height: {matchResult.height}</p>
          </div>
        ))}
    </>
  );
}
export default Search;
