import React, { useState, useEffect } from "react";
import {
  Card,
  Grid,
  GridColumn,
  CardContent,
  CardHeader,
  CardDescription,
} from "semantic-ui-react";

function Films() {
  const [films, setFilms] = useState([]);
  useEffect(() => {
    var url = "https://swapi.dev/api/films";
    async function fetchFilms() {
      let res = await fetch(url);
      let data = await res.json();
      setFilms(data.results);
    }
    fetchFilms();
  }, []);

  const person = "Luke Skywalker";
  const tomb = [];

  const [searchResult, setSearchResult] = useState([]);
  useEffect(() => {
    for (let i = 1; i < 83; i++) {
      var url = "https://swapi.dev/api/people/" + i;
      async function fetchAll() {
        let res = await fetch(url);
        let allData = await res.json();
        setSearchResult(allData.results);
        tomb.push(allData.name);
        
      }

      console.log(tomb);
    }
    fetchAll();
  }, []);
  function find() {
    alert("helo");
  }

  return (
    <>
      <h1>Films</h1>
      {
        <Grid columns={3}>
          {films.map((films, i) => {
            return (
              <GridColumn key={i}>
                <Card>
                  <CardContent>
                    <CardHeader>Episode: {films.episode_id}</CardHeader>
                    <CardDescription>
                      <strong>Title</strong>
                      <p>{films.title}</p>
                    </CardDescription>
                  </CardContent>
                </Card>
              </GridColumn>
            );
          })}
        </Grid>
      }
      <div className="search-div">
        <h1>Searhing</h1>
        <input className="search-input" type="text" />
        <button id="findButton" onClick={find}>
          Find
        </button>
      </div>
    </>
  );
}
export default Films;
