import React, { useCallback, useState } from "react";
import axios from "axios";


const initialState = {
  name: "",
  height: "",
  mass: "",
  gender: "",
};
function Random() {
  const [character, setCharacter] = useState(initialState);
  const [loading, setLoading] = useState(false);
  let visible = false;

  const getInfo = useCallback(() => {
    visible = true;
    setLoading(true);
    const randNum = Math.floor(Math.random() * 82 + 1);
    const url = "https://swapi.dev/api/people/" + randNum;
    axios
      .get(url)
      .then(function (response) {
        setCharacter(response.data);
        setLoading(false);
      })
      .catch((e) => {
        setCharacter(initialState);
        setLoading(false);
      });
  }, []);

  return (
    <div className="random-div">
      <h1>Random Character</h1>
      <div className="info-container">
        {loading && <div className="spinner"><i class="fas fa-circle-notch fa-spin fa-2x"></i></div>}
        {visible  && <div></div> }
            
      </div>
      <button id="randomCharButton" onClick={getInfo}>
        Get a random Character
      </button>
    </div>
  );
}
export default Random;
