import React, { useState, useEffect } from "react";
import {Card, Grid, GridColumn, CardContent, CardHeader, CardDescription} from "semantic-ui-react";
import debounce from "lodash/debounce";
 
const fetchPlanets = debounce( async (url, setPlanets) => {
    let res = await fetch(url);
    let data = await res.json();
    setPlanets(data.results);
},300)

function Planets() {

    var [pageNum, setpageNum] = useState(1);
    
    const [planets, setPlanets] = useState([]);
    useEffect(debounce(()  => {
        var url = "https://swapi.dev/api/planets/?page=" + pageNum;
          fetchPlanets(url, setPlanets);
    },300),[pageNum])

    function Add() {
        if(pageNum < 6) {
         pageNum++;
         setpageNum(pageNum)
        }
    }
    function Prev() {
        if(pageNum > 1) {
         pageNum--;
         setpageNum(pageNum)
        }
    }

    return (
        <>
            <h1>Planets</h1>
           { <Grid columns={3}>
                {planets.map((planets,i) => {
                    return(
                        <GridColumn key={i}>
                            <Card>
                                <CardContent>
                                    <CardDescription>
                                        <h1 className="cardHeader">{planets.name}</h1>
                                        <strong className="cardContent">Climate</strong>
                                        <p className="cardContent">{planets.climate}</p>
                                        <strong className="cardContent">Gravity</strong>
                                        <p className="cardContent">{planets.gravity}</p>
                                        <strong className="cardContent">Terrain</strong>
                                        <p className="cardContent">{planets.terrain}</p>
                                        <strong className="cardContent">Population</strong>
                                        <p className="cardContent">{planets.population}</p>
                                    </CardDescription>
                                </CardContent>
                            </Card>
                        </GridColumn>
                    )
                })}
            </Grid>}
             <div id="prev-next-div">
                 <a id="prevButton"  onClick={Prev} className="next round">&#8249;</a>
                 <a id="nextButton"  onClick={Add} className="next round">&#8250;</a>
            </div>
            
        </>
    )
}
export default Planets;
