import React, { useState, useEffect } from "react";
import {Card, Grid, GridColumn, CardContent, CardHeader, CardDescription} from "semantic-ui-react";
import { Container, Dimmer, Loader } from "semantic-ui-react";


function People() {

    var [pageNum, setpageNum] = useState(1);
    var url = "https://swapi.dev/api/people/?page=" + pageNum;
   
    const [people, setPeople] = useState([]);
    useEffect(() => {
        async function fetchPeople() {
            let res = await fetch(url);
            let data = await res.json();
            setPeople(data.results);
           // setLoading(false);
          }
          fetchPeople();
    })

    function Add() {
        if(pageNum < 9) {
         pageNum++;
         setpageNum(pageNum)
         url = "https://swapi.dev/api/people/?page=" + pageNum;
         console.log(url);
        }
    }
    function Prev() {
        if(pageNum > 1) {
         pageNum--;
         setpageNum(pageNum)
         url = "https://swapi.dev/api/people/?page=" + pageNum;
         console.log(url);
        }
    }

    return (
        <>
              {loading ? (
          <Dimmer active inverted>
            <Loader inverted>Loading</Loader>
          </Dimmer>
        ) : (
          <Switch>
            <Route exact path="/">
              <Home />
            </Route>
            <Route path="/people">
              <People />
            </Route>
            <Route path="/planets">
              <Planets data={planets} />
            </Route>
            <Route path="/starships">
              <Starships data={starships} />
            </Route>
            <Route path="/policies">
              <Policies />
            </Route>
          </Switch>
        )}
                <h1>People</h1>
                <Grid columns={3}>
                    {people.map((people,i) => {
                        return(
                            <GridColumn key={i}>
                                <Card>
                                    <CardContent>
                                        <CardHeader>{people.name}</CardHeader>
                                        <CardDescription>
                                            <strong>Height</strong>
                                            <p>{people.height}</p>
                                            <strong>Mass</strong>
                                            <p>{people.mass}</p>
                                            <strong>Gender</strong>
                                            <p>{people.gender}</p>
                                        </CardDescription>
                                    </CardContent>
                                </Card>
                            </GridColumn>
                        )
                    })}
                </Grid>
                <div id="prev-next-div">
                    <a id="prevButton"  onClick={Prev} className="next round">&#8249;</a>
                    <a id="nextButton"  onClick={Add} className="next round">&#8250;</a>
                </div>
                
        </>
    )
}
export default People;
