import React, { useEffect, useState } from "react";
import {
    Card,
    CardContent,
    CardDescription,
  } from "semantic-ui-react";
import axios from "axios";

function Character({id}) {

  const [result, setResult] = useState({})

  useEffect(() => {
    axios("https://swapi.dev/api/people/" + id).then((res) =>
    {
        return setResult(res.data.result);
      })
  },[id]);
  return (
    <>
      <h1>Character Info</h1>
      <Card>
        <CardContent>
          <CardDescription>
            <h1>Name</h1>
            <strong className="cardContent">Height</strong>
            <p className="cardContent">{props.height}</p>
            <strong className="cardContent">Mass</strong>
            <p className="cardContent">{props.mass}</p>
            <strong className="cardContent">Gender</strong>
            <p className="cardContent">{props.gender}</p>
          </CardDescription>
        </CardContent>
      </Card>
    </>
  );
}
export default Character;
