import React from "react";
import {Card, Grid, GridColumn, CardContent, CardHeader, CardDescription} from "semantic-ui-react";


function People( {data} ) {
    return (
        <>
            <h1>People</h1>
            <Grid columns={3}>
                {data.map((people,i) => {
                    return(
                        <GridColumn key={i}>
                            <Card>
                                <CardContent>
                                    <CardHeader>{people.name}</CardHeader>
                                    <CardDescription>
                                        <strong>Height</strong>
                                        <p>{people.height}</p>
                                        <strong>Mass</strong>
                                        <p>{people.mass}</p>
                                        <strong>Gender</strong>
                                        <p>{people.gender}</p>
                                    </CardDescription>
                                </CardContent>
                            </Card>
                        </GridColumn>
                    )
                })}
            </Grid>
            <a id ="prevButton" href="#" class="next round">&#8249;</a>
            <a  id ="nextButton" href="#" class="next round">&#8250;</a>
        </>
    )
}
export default People;
